import React from "react";
import { useState } from "react";
import "../App.css";
import dayjs from "dayjs";
import kc from "./kelvinToCelcius";

const api = "d929b80c2587abdc6dbda7f96c9ff6c5";

////////////////////////////

class Weather {
  constructor(temp, weather, date, icon) {
    this.date = date;
    this.temp = temp;
    this.weather = weather;
    this.icon = icon;
  }
}
///////////////////////////

function Search(props) {
  const [data, setData] = useState("");
  const [forecastHourlyState, setForecastHourlyState] = useState("");
  const [forecastDailyState, setForecastDailyState] = useState("");

  //////////////////////////

  let forecastDaily = [];
  let test = [];
  let forecastHourly = [];

  let x = 0;
  let y = 0;
  //////////////////////////

  function keyPress(a) {
    if (a.key === "Enter") {
      const date = new Date();
      const cityValue = a.target.value;
      // Fetch to get longitutde and latitude to use with second API - Translate String to coordinates
      fetch(
        `https://api.openweathermap.org/data/2.5/weather?q=${cityValue}&appid=${api}`
      )
        .then((response) => response.json())
        .then((data) => {
          // Fetching weather data with coordinates - Current - Forecast
          fetch(
            `https://api.openweathermap.org/data/2.5/onecall?lat=${data.coord.lat}&lon=${data.coord.lon}&appid=${api}`
          )
            .then((response) => response.json())
            .then((data) => {
              console.log(date.getDay());

              data.daily.forEach((element) => {
                forecastDaily[x] = new Weather(
                  kc(element.temp.day),
                  element.weather[0].main + " ",
                  dayjs().add(x, "day").format("dddd DD/MM"),
                  element.weather[0].icon
                );
                x++;
              });

              setForecastDailyState(
                forecastDaily.map((a) => (
                  <li>
                    {a.date} / {a.temp} / {a.weather}
                  </li>
                ))
              );

              x = 0;

              data.hourly.forEach((element) => {
                forecastHourly[x] = new Weather(
                  kc(element.temp),
                  element.weather[0].main + " ",
                  `H + ${x} : `,
                  element.weather[0].icon
                );
                x++;
              });

              setForecastHourlyState(
                forecastHourly.map((a) => (
                  <li>
                    {a.date} / {a.temp} / {a.weather}
                  </li>
                ))
              );

              data.hourly.forEach((element) => {
                forecastHourly[y] = kc(element.temp) + " ";
                y++;
              });
            });
        })

        .catch((error) => {
          console.log("error");
        });
    }
  }

  return (
    <div>
      <input onKeyPress={keyPress} />

      <div className="bgYellow textBlack0">
        <div className="bgRed textWhite1">Today</div>
      </div>

      <div className="flex row">
<div>
      <div className="bgGrass">Daily Forecast</div>
      <div className="bgGreen">{forecastDailyState}</div>
      </div> 
     <div>
      <div className="bgDusk">Hourly Forecast</div>
      <div className="bgOrange">{forecastHourlyState}</div>
      </div>
      </div>
    </div>
  );
}

export default Search;
