import React from "react";
import { useState } from "react";
import Search from "./components/Search";
import "./App.css";
import Nav from "./components/Nav";
import About from "./components/About";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";

var authObject = firebase.auth();
var firebaseAuthObject = $firebaseAuth(authObject);
var ui = new firebaseui.auth.AuthUI(authObject);


 // FirebaseUI config.
 var uiConfig = {
  callbacks: {
     signInSuccess: vm.callback,
     uiShown: function() {
        document.getElementById('loader').style.display = 'none';
     }
  },
  redirectUrl: '',
  credentialHelper: firebaseui.auth.CredentialHelper.ACCOUNT_CHOOSER_COM,
  queryParameterForWidgetMode: 'mode',
  signInFlow: 'popup',
  signInOptions: [
     firebase.auth.EmailAuthProvider.PROVIDER_ID
  ]
};
// The start method will wait until the DOM is loaded.
authService.firebaseUI.start('#firebaseui-auth-container', uiConfig);

class App extends React.Component {
  render() {
    return (
      <div className="App  bgBlack1 h100 textWhite1">
        <div className=" overflowAuto h100 flex jcc column aic">
        <div id="firebaseui-auth-container"></div>
          <Search/>
        </div>
      </div>
    );
  }
}

export default App;
