import React from "react";
import { useState } from "react";

const api = "e40f650c2214e7883b9a3e4cd28fb910";
const testCity = "Paris";

function Search(props) {
  // <= //
  const [weather, setWeather] = useState("FUCK");

  const keyPress = (a) => {
    if (a.key === "Enter") {
      fetch(
        `https://api.openweathermap.org/data/2.5/weather?q=${testCity}&appid=${api}`
      )
        .then((response) => response.json())
        .then((data) => {
          setWeather(data)
        })

        .catch((error) => {
          this.setState({ ccc: "Invalid Input" });
        });
    }
  };

  return <input onKeyPress={keyPress} />;
} // <= //

export default Search;
