import React from "react";
import { useState } from "react";
import "../App.css";

const api = "d929b80c2587abdc6dbda7f96c9ff6c5";
const testCity = "Paris";

function Search(props) {
  const [weather, setWeather] = useState("");
  const [location, setLocation] = useState("");
  const [longitude, setLongitude] = useState("");
  const [latitude, setLatitude] = useState("");

  const keyPress = (a) => {
    if (a.key === "Enter") {
      fetch(
        `https://api.openweathermap.org/data/2.5/weather?q=${testCity}&appid=${api}`
      )
        .then((response) => response.json())
        .then((data) => {

          console.log(data);

          setLocation(data.name);
          setWeather(data.weather[0].main);
          setLongitude(data.coord.lon);
          setLatitude(data.coord.lat);
          setTemperature(data.main.temp);
          setWindSpeed(data.wind.speed);
        })

        .catch((error) => {
          console.log("error");
        });
    }
  };

  return (
    <div>
      <input onKeyPress={keyPress} />

      <div className="flex column"> Location : {location}</div>
      <div>Weather : {weather}</div>
      <div>Longitutde : {longitude}</div>
      <div>Latitude : {latitude}</div>

    </div>
  );
}

export default Search;
