import React from 'react';
import StyledFirebaseAuth from 'react-firebaseui/StyledFirebaseAuth';
import firebase from 'firebase';
import { useState } from 'react'
import "./firebaseConfig"
import {api} from "./api"





firebase.database().ref('meteo/').set({
  1: "Paris",
  2: "Rio",
  3: "Tokyo",
  4: "Vancouver"
});

