export default function Hello() {

  const [dbvalue, setdbvalue] = useState("");
  const [idLength, setidLength] = useState("");
  const [name, setname] = useState("");

  const data = firebase.database().ref('meteo/');
  data.once('value').then((a) => { console.log(a.val()) })

  const show = () => {
    const data = firebase.database().ref('meteo/');
    data.once('value').then((a) => { setdbvalue(a.val()); })

  }

  const input = (e) => {
    console.log(e.target.value)
    setname(e.target.value)
    const data = firebase.database().ref('meteo/');
    data.once('value').then((a) => { setidLength(a.val().length) })
    console.log(idLength)
  }

  const add = () => {
    firebase.database().ref('meteo/').set({
      6: name,

    });
  }

  function Search(props) {
    const [currentState, setCurrentState] = useState("");
    const [currentState1, setCurrentState1] = useState("");
    const [currentState2, setCurrentState2] = useState("");
    const [currentState3, setCurrentState3] = useState("");
    const [forecastHourlyState, setForecastHourlyState] = useState("");
    const [forecastDailyState, setForecastDailyState] = useState("");
    const [errorState, setErrorState] = useState("");
    const [name, setName] = useState("");

    const settings = {
      dots: false,
      infinite: true,
      speed: 500,
      slidesToShow: 3,
      arrows: true,
      slidesToScroll: 3
    };

    const settingsHourly = {
      dots: false,
      infinite: true,
      speed: 500,
      slidesToShow: 6,
      slidesToScroll: 6
    };

    let forecastDaily = [];
    let forecastHourly = [];

    let x = 0;
    let y = 0;

    useEffect(() => {
      if (name === "") keyPress({ key: "Enter", target: { value: "Paris" } })
    });



    //////////////////////////

    function keyPress(a) {
      if (a.key === "Enter") {
        setErrorState("");
        const date = new Date();
        const cityValue = a.target.value;
        fetch(
          `https://api.openweathermap.org/data/2.5/weather?q=Paris&appid=${api}` // Fetch to get longitutde and latitude to use with second API - Translate String to coordinates
        )
          .then((response) => response.json())
          .then((data) => {
            setName(data.name);
            // Fetching weather data with coordinates - Current - Forecast
            fetch(
              `https://api.openweathermap.org/data/2.5/onecall?lat=${data.coord.lat}&lon=${data.coord.lon}&appid=${api}`
            )
              .then((response) => response.json())
              .then((data) => {
                console.log(date.getDay());

                const icon = data.current.weather[0].icon;
                const iconURL = ` http://openweathermap.org/img/wn/${icon}@2x.png`;
                //////////////////////////
                console.log(data);
                setCurrentState(
                  <div className="flex column jcc  aic">
                    <br />
                    <div>{kc(data.current.temp)}Â°C</div>
                    <div>{data.current.weather[0].main}</div>
                    <img src={iconURL}></img>
                  </div>
                );
              });
          })
          //////////////////////////

          .catch((error) => {
            console.log("error");
            setErrorState("City name does not exist");
            setCurrentState("");
            setForecastDailyState("");
            setForecastHourlyState("");
          });
      }

    }



  return (
    <div className="flex column  h50 jcc aic ">

      <button onClick={show}>Show Favorites </button>
      <div>{dbvalue}</div>
      <br />
      <br />
      <br />
      <input onKeyPress={input}></input>
      <button onClick={add}>Add to Favorites </button>



    </div>
  )

  }
}
