import React from "react";
import { useState } from "react";
import "../App.css";

const api = "d929b80c2587abdc6dbda7f96c9ff6c5";
const testCity = "Paris";

function Search(props) {
  const [weather, setWeather] = useState("");
  const [location, setLocation] = useState("");
  const [longitude, setLongitude] = useState("");
  const [latitude, setLatitude] = useState("");
  const [windSpeed, setWindSpeed] = useState("");
  const [temperature, setTemperature] = useState("");

  async function keyPress(a) {
    if (a.key === "Enter") {
      const cityValue = a.target.value;

      await fetch(
        `https://api.openweathermap.org/data/2.5/weather?q=${cityValue}&appid=${api}`
      )
        .then((response) => response.json())
        .then(async(data) => {
          console.log(data);

          setLocation(data.name);
          setWeather(data.weather[0].main);
          setLongitude(data.coord.lon);
          setLatitude(data.coord.lat);
          setTemperature(data.main.temp);
          setWindSpeed(data.wind.speed);
        })

        .then(
          await fetch(
            `https://api.openweathermap.org/data/2.5/onecall?lat=${latitude}&lon=${longitude}&appid=${api}`
          )
        .then((response) => response.json())
        .then((data) => { 
          console.log(data)
        })

        )

        .catch((error) => {
          console.log("error");
        });
    }
  };

  return (
    <div>
      <input onKeyPress={keyPress} />

      <div className="flex column"> Location : {location}</div>
      <div>Weather : {weather}</div>
      <div>Longitutde : {longitude}</div>
      <div>Latitude : {latitude}</div>
      <div>Temperature : {temperature - 273.15} *C</div>
      <div>Wind Speed : {windSpeed}</div>
    </div>
  );
}

export default Search;
