import React from "react";
import { useState } from "react";
import "../App.css"


const api = "d929b80c2587abdc6dbda7f96c9ff6c5";
const testCity = "Paris";

function Search(props) {
  const [weather, setWeather] = useState("");
  const [location, setLocation] = useState("");

  const keyPress = (a) => {
    if (a.key === "Enter") {
      fetch(
        `https://api.openweathermap.org/data/2.5/weather?q=${testCity}&appid=${api}`
      )
        .then((response) => response.json())
        .then((data) => {
          setWeather(data);

          console.log(data);

          console.log(data.name);
          setLocation(data.weather[0].main);
          console.log(data.coord.lon);
          console.log(data.coord.lat);
          console.log(data.main.temp);
          console.log(data.wind.speed);
        })

        .catch((error) => {
          console.log("error");
        });
    }
  };

  return (
    <div>
      <input onKeyPress={keyPress} />

      <div className="flex column bgBlack0">
      {weather.name}

      {location}
      {weather.name}
      {weather.name}
        {weather.name}

        </div>
    </div>
  );
}

export default Search;
