import React from "react";
import { useState } from "react";
import Search from "./components/Search";
import "./App.css";
import Nav from "./components/Nav";
import About from "./components/About";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";

import FirebaseLogin from './components/FirebaseLogin'



class App extends React.Component {

  constructor(props) {
    super(props);
    this.state = {loginView: ""};
  }

  a = () =>{
    this.setState({loginView: "none"})
    console.log(this.state.loginView)
  }


  render() {
    return (
      <div className="App  bgBlack1 h100 textWhite1" onClick={a}>
        <div id="firebaseLoginContainer" style={{
          left: "50%",
          top: "50%",
          transform: "translate(-50%, -50%)",
          borderRadius: "9px",
          zIndex:"99"
        }} className={"absolute flex bgWhite1 " + this.state.loginView} >
          <FirebaseLogin />


        </div>




        <div className=" overflowAuto h100 flex jcc column aic">
          <Search />
        </div>
      </div>
    );
  }
}

export default App;
